import React, { useRef, useState } from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  useScrollTrigger,
  useMediaQuery,
  Drawer,
  List,
  Divider,
  Hidden,
} from "@material-ui/core";
import {
  AccountCircleOutlined as AccountsIcon,
  DashboardOutlined as TileViewIcon,
  Brightness4Outlined as ToggleDarkModeIcon,
  Brightness5Outlined as ToggleLightModeIcon,
  SearchOutlined as SearchIcon,
  Menu as MenuIcon,
  WbIncandescentOutlined as IdeaIcon,
  LabelOutlined as LabelIcon,
} from "@material-ui/icons";
import CalendarTodayOutlinedIcon from "@material-ui/icons/CalendarTodayOutlined";

import ProfilePopover from "./components/ProfilePopover";
import SearchBar from "./components/SearchBar";
import DrawerItem from "./components/DrawerItem";

// // import { useUserStore, useUiStore } from "../../store";
// // import { useMutation } from "urql";
// // import { updateUser } from "../../gql";

import CssBaseline from "@material-ui/core/CssBaseline";

const drawerWidth = 275;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  grow: {
    flexGrow: 1
  },
  containerBorder: {
    borderBottomStyle: "solid",
    borderBottomWidth: "1px",
    borderBottomColor: theme.palette.divider,
  },
  menuButton: {
    [theme.breakpoints.up("sm")]: {
      marginRight: theme.spacing(1),
    },
    marginRight: 8,
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
    border: 0,
  },
  appBar: {
    [theme.breakpoints.up("sm")]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  drawerPaper: {
    width: drawerWidth,
    background: theme.palette.background.default,
    border: 0,
    paddingBottom: theme.spacing(2),
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  logoContainer: {
    display: "flex",
    justifyContent: "stretch",
  },
  logo: {
    height: theme.spacing(5.5),
    padding: theme.spacing(0, 1, 0, 0),
    [theme.breakpoints.up("sm")]: {
      display: "block",
    },
  },
  title: {
    ...theme.custom.fontFamily.metropolis,
    display: "none",
    [theme.breakpoints.up("xs")]: {
      display: "flex",
      alignItems: "center",
    },
  },
  sectionTitle: {
    padding: theme.spacing(2, 1, 0, 2),
    color: theme.palette.text.secondary,
  },
    searchbarContainer: {
    flexGrow: 1,
    marginLeft: theme.spacing(7),
    [theme.breakpoints.up("md")]: {
      flexGrow: 0,
      width: theme.spacing(60),
      marginLeft: theme.spacing(5),
    },
  },
  datepicker: {
    //depends if i disable toolbar
    //paddingTop: theme.spacing(2),
    width: "275px",
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    display: "inline-flex",
    paddingLeft: "25px",
    marginBottom: "-13px",
    minHeight: "68px"
    // [theme.breakpoints.down("sm")]: {
    //   display: "none",
    // }
  },
}));

export default function ResponsiveDrawer(props) {
  const menuId = "primary-search-account-menu";
  const classes = useStyles();
  const theme = useTheme();
  const { window } = props;

  const [isProfilePopoverOpen, setProfilePopoverOpen] = useState(false);
  const [isSearchShowingInMobile, setSearchShowing] = useState(false);
  const [selectedLabelId, setSelectedLabelId] = useState(0);

  const [date, changeDate] = useState(new Date());
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const profileMenuRef = useRef();
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });
  const isMobile = useMediaQuery(theme.breakpoints.down("xs"));
  const isTablet = useMediaQuery(theme.breakpoints.down("sm"));

  //const [projectsItems] = useProjectsStore();
  const projectsItems = [
    { id: 1, name: "project-1" },
    { id: 2, name: "project-2" },
    { id: 3, name: "project-3" },
    { id: 4, name: "project-4" },
    { id: 5, name: "project-5" },
    { id: 6, name: "project-6" },
    { id: 7, name: "project-7" },
  ];

  const onDrawerItemSelected = (labelId) => {
    setSelectedLabelId(labelId);
  };

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <div className={classes.toolbar}>
        <IconButton
          edge="start"
          aria-label="open drawer"
          onClick={handleDrawerToggle}
          color="inherit"
          className={classes.menuButton}
        >
          <MenuIcon htmlColor={theme.custom.palette.iconColor} />
        </IconButton>
        <LogoContainer />
      </div>
      <Divider />
      <div className={classes.sectionTitle}>
        <Typography variant="overline" component="span">
          Projects
        </Typography>
      </div>
      <DrawerItem
        key={0}
        text={"All Projects"}
        icon={<IdeaIcon htmlColor={theme.custom.palette.iconColor} />}
        isSelected={selectedLabelId === 0}
        onClick={() => onDrawerItemSelected(0)}
      />
      <div>
        <List>
          {projectsItems.map((projectsItem) => (
            <DrawerItem
              key={projectsItem.id}
              text={projectsItem.name}
              icon={<IdeaIcon htmlColor={theme.custom.palette.iconColor} />}
              isSelected={selectedLabelId === projectsItem.id}
              onClick={() => onDrawerItemSelected(projectsItem.id)}
            />
          ))}
        </List>
      </div>
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        elevation={trigger ? 4 : 0}
        className={trigger ? null : classes.containerBorder}
        position="fixed"
      >
        <Toolbar>
          {isTablet ? (
            <IconButton
              edge="start"
              aria-label="open drawer"
              onClick={handleDrawerToggle}
              color="inherit"
              className={classes.menuButton}
            >
              <MenuIcon htmlColor={theme.custom.palette.iconColor} />
            </IconButton>
          ) : null}

          {isMobile ? (
            isSearchShowingInMobile ? (
              <SearchContainer onSearchClose={() => setSearchShowing(false)} />
            ) : (
              <LogoContainer />
            )
          ) : (
            <>
              <LogoContainer />
              <SearchContainer onSearchClose={() => setSearchShowing(false)} />
            </>
          )}
          <div className={classes.grow} />
          {isMobile && !isSearchShowingInMobile ? (
            <div>
              <IconButton
                aria-label="search"
                aria-controls={menuId}
                onClick={() => setSearchShowing(true)}
              >
                <SearchIcon htmlColor={theme.custom.palette.iconColor} />
              </IconButton>
            </div>
          ) : null}
          <div>
            <IconButton aria-label="calendar view">
              <CalendarTodayOutlinedIcon
                htmlColor={theme.custom.palette.iconColor}
              />
            </IconButton>
          </div>
          <IconButton aria-label={"list view"} aria-controls={menuId}>
            <TileViewIcon htmlColor={theme.custom.palette.iconColor} />
          </IconButton>
          <div>
            <IconButton
              edge="end"
              ref={profileMenuRef}
              aria-label="account of current user"
              aria-controls={menuId}
              aria-haspopup="true"
              onClick={() => setProfilePopoverOpen(true)}
            >
              <AccountsIcon htmlColor={theme.custom.palette.iconColor} />
            </IconButton>
          </div>
        </Toolbar>
        <ProfilePopover
