import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import SummaryBox from "./SummaryBox";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "25ch",
    },
    display: "flex",
    flexWrap: "wrap"
  },
  content: {
    align: "center",
    display: "block",
    position: "relative",
    top: "35px",
  },
  summaryContainer: {
    display: "flex",
    padding: theme.spacing(4, 0),
    margin: "0 auto",
  },
  summaryWrapper: {
    flex: 1,
    maxWidth: theme.spacing(85),
    maxHeight: theme.spacing(85),
    margin: "0 auto",
  },
  title: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    fontSize: "16px",
  },
  titleInput: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    width: "20px",
    textAlign: "center",
  },
}));

export default function () {
  const classes = useStyles();
  const [value, setValue] = React.useState("Controlled");

  const handleChange = (event) => {
    setValue(event.target.value);
  };
  return (
    <div className={classes.content}>
      <div className={classes.summaryContainer}>
        <div className={classes.summaryWrapper}>
          <span className={classes.title}>
            Summarize my text in
            <input className={classes.titleInput} type="text" />
            sentences.
          </span>

          <form className={classes.root} noValidate autoComplete="off">
            <TextField
              id="outlined-full-width"
              label="Summary"
              style={{ margin: 8 }}
              placeholder="Paste an article, text or essay in this box and hit summarize; 
              we'll return a shortened copy!!"
              fullWidth
              multiline
              margin="normal"
              InputLabelProps={{
                shrink: true,
              }}
              rows={8}
              variant="outlined"
            />
          </form>
          <SummaryBox />
        </div>
      </div>
    </div>
  );
}
