import React, { useRef, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import SummaryBox from "./SummaryBox";

const useStyles = makeStyles((theme) => ({
  content: {
    align: "center",
    display: "block",
    position: "relative",
    top: "35px",
  },
  summaryContainer: {
    display: "flex",
    padding: theme.spacing(4, 0),
    margin: "0 auto",
  },
  summaryWrapper: {
    flex: 1,
    maxWidth: theme.spacing(85),
    maxHeight: theme.spacing(85),
    margin: "0 auto",
  },
  title: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    fontSize: "16px",
  },
  titleInput: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    width: "20px",
    textAlign: "center",
  },
  searchbarContainer: {
    flexGrow: 1,
    marginLeft: theme.spacing(7),
    [theme.breakpoints.up("md")]: {
      flexGrow: 0,
      width: theme.spacing(60),
      marginLeft: theme.spacing(5),
    },
  },
}));

export default function () {
  const classes = useStyles();
  const [setSearchShowing] = useState(false);

  return (
    <div className={classes.content}>
      <div className={classes.summaryContainer}>
        <div className={classes.summaryWrapper}>
          <span className={classes.title}>
            Summarize my text in
            <input className={classes.titleInput} type="text" />
            sentences.
          </span>
          <SummaryContainer onSearchClose={() => setSearchShowing(false)} />
        </div>
      </div>
    </div>
  );
}

function SummaryContainer({ onSearchClose }) {
  const classes = useStyles();
  return (
    <div className={classes.summaryContainer}>
      <SummaryBox ml={8} onSearchClose={onSearchClose} />
    </div>
  );
}
