import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from '@material-ui/core/TextField';
import SummaryBox from "./SummaryBox";

const useStyles = makeStyles((theme) => ({
  content: {
    align: "center",
    display: "block",
    position: "relative",
    top: "35px",
  },
  summaryContainer: {
    display: "flex",
    padding: theme.spacing(4, 0),
    margin: "0 auto",
  },
  summaryWrapper: {
    flex: 1,
    maxWidth: theme.spacing(85),
    maxHeight: theme.spacing(85),
    margin: "0 auto",
  },
  title: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    fontSize: "16px",
  },
  titleInput: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    width: "20px",
    textAlign: "center",
  },
}));

export default function () {
  const classes = useStyles();
  return (
    <div className={classes.content}>
      <div className={classes.summaryContainer}>
        <div className={classes.summaryWrapper}>
          <span className={classes.title}>
            Summarize my text in
            <input className={classes.titleInput} type="text" />
            sentences.
          </span>
          <TextField
            id="outlined-full-width"
            label="Summary"
            style={{ margin: 8 }}
            placeholder="Summa summarizes text to save you time. &#10;Paste an article, text or essay in this box and hit summarize; we'll return a shortened copy"
            fullWidth
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
            variant="outlined"
          />
          <SummaryBox />
        </div>
      </div>
    </div>
  );
}
