import React, { useRef, useState } from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  useScrollTrigger,
  useMediaQuery,
  Drawer,
  List,
  Divider,
  Hidden,
} from "@material-ui/core";
import {
  AccountCircleOutlined as AccountsIcon,
  DashboardOutlined as TileViewIcon,
  Brightness4Outlined as ToggleDarkModeIcon,
  Brightness5Outlined as ToggleLightModeIcon,
  SearchOutlined as SearchIcon,
  Menu as MenuIcon,
  WbIncandescentOutlined as IdeaIcon,
  LabelOutlined as LabelIcon,
} from "@material-ui/icons";
import CalendarTodayOutlinedIcon from "@material-ui/icons/CalendarTodayOutlined";

import ProfilePopover from "./components/ProfilePopover";

import CssBaseline from "@material-ui/core/CssBaseline";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  grow: {
    flexGrow: 1
  },
  containerBorder: {
    borderBottomStyle: "solid",
    borderBottomWidth: "1px",
    borderBottomColor: theme.palette.divider,
  },
  menuButton: {
    [theme.breakpoints.up("sm")]: {
      marginRight: theme.spacing(1),
    },
    marginRight: 8,
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  logoContainer: {
    display: "flex",
    justifyContent: "stretch",
  },
  logo: {
    height: theme.spacing(5.5),
    padding: theme.spacing(0, 1, 0, 0),
    [theme.breakpoints.up("sm")]: {
      display: "block",
    },
  },
  title: {
    ...theme.custom.fontFamily.metropolis,
    display: "none",
    [theme.breakpoints.up("xs")]: {
      display: "flex",
      alignItems: "center",
    },
  },
  sectionTitle: {
    padding: theme.spacing(2, 1, 0, 2),
    color: theme.palette.text.secondary,
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    display: "inline-flex",
    paddingLeft: "25px",
    marginBottom: "-13px",
    minHeight: "68px"
    // [theme.breakpoints.down("sm")]: {
    //   display: "none",
    // }
  },
}));

export default function ResponsiveDrawer(props) {
  const menuId = "primary-search-account-menu";
  const classes = useStyles();
  const theme = useTheme();
  const { window } = props;

  const [isProfilePopoverOpen, setProfilePopoverOpen] = useState(false);
  const [isSearchShowingInMobile, setSearchShowing] = useState(false);
  const [selectedLabelId, setSelectedLabelId] = useState(0);

  const [date, changeDate] = useState(new Date());
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const profileMenuRef = useRef();
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });
  const isMobile = useMediaQuery(theme.breakpoints.down("xs"));
  const isTablet = useMediaQuery(theme.breakpoints.down("sm"));

  const onDrawerItemSelected = (labelId) => {
    setSelectedLabelId(labelId);
  };

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };


  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        elevation={trigger ? 4 : 0}
        className={trigger ? null : classes.containerBorder}
        position="fixed"
      >
        <Toolbar>
          {isTablet ? (
            <IconButton
              edge="start"
              aria-label="open drawer"
              onClick={handleDrawerToggle}
              color="inherit"
              className={classes.menuButton}
            >
              <MenuIcon htmlColor={theme.custom.palette.iconColor} />
            </IconButton>
          ) : null}

          <LogoContainer />

          <div className={classes.grow} />

          <div>
            <IconButton aria-label="calendar view">
              <CalendarTodayOutlinedIcon
                htmlColor={theme.custom.palette.iconColor}
              />
            </IconButton>
          </div>
          <IconButton aria-label={"list view"} aria-controls={menuId}>
            <TileViewIcon htmlColor={theme.custom.palette.iconColor} />
          </IconButton>
          <div>
            <IconButton
              edge="end"
              ref={profileMenuRef}
              aria-label="account of current user"
              aria-controls={menuId}
              aria-haspopup="true"
              onClick={() => setProfilePopoverOpen(true)}
            >
              <AccountsIcon htmlColor={theme.custom.palette.iconColor} />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

    </div>
  );
}

function LogoContainer() {
  const classes = useStyles();
  return (
    <div className={classes.logoContainer}>
      <img className={classes.logo} src={'../../logo.png'} alt={"logo"} />
      <Typography
        color="textSecondary"
        className={classes.title}
        variant="h4"
        noWrap
      >
        summa
      </Typography>
    </div>
  );
}
