import React, { useRef, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import SummaryBox from "./SummaryBox";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: '25ch',
  },
  content: {
    align: "center",
    display: "block",
    position: "relative",
    top: "35px",
  },
  summaryContainer: {
    display: "flex",
    padding: theme.spacing(4, 0),
    margin: "0 auto",
  },
  summaryWrapper: {
    flex: 1,
    maxWidth: theme.spacing(85),
    maxHeight: theme.spacing(85),
    margin: "0 auto",
  },
  title: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    fontSize: "16px",
  },
  titleInput: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    width: "20px",
    textAlign: "center",
  },
  searchbarContainer: {
    flexGrow: 1,
    marginLeft: theme.spacing(7),
    [theme.breakpoints.up("md")]: {
      flexGrow: 0,
      width: theme.spacing(60),
      marginLeft: theme.spacing(5),
    },
  },
}));

export default function () {
  const classes = useStyles();

  return (
    <div className={classes.content}>
      <div className={classes.summaryContainer}>
        <div className={classes.summaryWrapper}>
          <div className={classes.summaryWrapper}>
            <span className={classes.titleInput}>
              Summarize my text in
              <input className={classes.titleInput} type="text" />
              sentences.
            </span>
          </div>
          <div className={classes.summaryContainer}>
            <SummaryBox ml={8} />
            <div className={classes.root}>
              <form noValidate autoComplete="off">
                <TextField
                  id="outlined-secondary"
                  label="Summa"
                  variant="outlined"
                  color="secondary"
                  style={{ margin: 8 }}
                  placeholder="Paste an article, text or essay in this box and hit summarize; we'll return a shortened copy for you to read.                  "
                  fullWidth
                  margin="normal"
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
