import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import SummaryBox from "./SummaryBox";

const useStyles = makeStyles((theme) => ({
  content: {
    align: "center",
    display: "block",
    position: "relative",
    top: "75px",
  },
  summaryBox: {
    padding: theme.spacing(3, 0),
    margin: "0 auto",
  },
  searchbarContainer: {
    flex: 1,
    maxWidth: theme.spacing(85),
    maxHeight: theme.spacing(85),
    margin: "0 auto",
  },
  summaryTitle: {
    width: "max-content",
    margin: "0 auto",
  },
  title: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    fontSize: "17px",
  },
  titleInput: {
    fontFamily: "Verdana, Arial, Helvetica, sans-serif",
    width: "20px",
    textAlign: "center",
  },
}));

export default function () {
  const classes = useStyles();

  return (
    <div className={classes.content}>
      <div className={classes.searchbarContainer}>
        <div className={classes.summaryTitle}>
          <span className={classes.title}>
            Summarize my text in &nbsp;
            <input
              className={classes.titleInput}
              type="text"
              autocomplete="off"
              maxlength="2"
              value="7"
            />
            &nbsp; sentences.
          </span>
        </div>
        <div className={classes.summaryBox}>
          <SummaryBox ml={8} />
        </div>
        <div style="position:relative;width:411px;height:38px;top:2px;left:0;margin:0;padding:0;">
          <div
            id="sm_upload_instance"
            style="position:absolute;left:0;width:144px;height:27px;margin:0;padding:0;"
          >
            <div class="qq-uploader">
              <div class="qq-upload-drop-area" style="display: none;">
                <span>Drop files here to upload</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
